Controls.Add(sbSelect);
sbSelect.AddMenu("All Files", x =>
{
	MessageBox.Show(x.Text);
});
sbSelect.AddMenu("Selected File", x =>
{
	MessageBox.Show(x.Text);
}, true);
sbSelect.AddMenu("Remove this Menu", x =>
{
	MessageBox.Show(x.Text);
	sbSelect.RemoveMenu(x.Text);
});

